import React from 'react';
import { PanelHeader, ModalRoot, ModalPage, View, Panel, ModalPageHeader, Button, PanelHeaderButton } from '@vkontakte/vkui';
import back from '../../resources/icons/back.svg'
const MODAL_PAGE_FILTERS = 'filters';

const MODAL_CARD_MONEY_SEND = 'money-send';


class MoodPicker extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeModal: null,
      modalHistory: []
    };


    this.modalBack = () => {
      this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
    };

  }
  componentDidMount(){
    this.setActiveModal(MODAL_PAGE_FILTERS)
  }
  setActiveModal(activeModal) {
    this.setState({activeModal : activeModal})
  };
  render() {
    const modal = (
        <ModalRoot activeModal={this.state.activeModal} onClose={this.modalBack}>
            <ModalPage  header={
            <ModalPageHeader  left={<PanelHeaderButton onClick={this.modalBack}><img onClick={() => {this.props.onBack(this.props.back)}} className="back_icon" src={back}/></PanelHeaderButton>}>
              Выберите настроение
            </ModalPageHeader>
          } id={MODAL_PAGE_FILTERS} onClose={this.modalBack}>
              <div className="navbar_line"/>
            </ModalPage>
        </ModalRoot>
    );
    return (
        <View activePanel="modals" modal={modal}>
          <Panel id="modals">

          </Panel>
        </View>
      );
  }
}

export default MoodPicker;
