        super(props)
        this.state = {

        }

    }

    render() {
        return (
            <div style={{width : (this.props.post.width + 'px'), height : (this.props.post.width + 'px')}} className="marker_bg">
                <div className="center_marker_cont">
                    <img className="marker_icon" src={this.props.post.category.icon}/>
                    {this.props.post.width > 100 && <p className="marker_title">{this.props.post.category.title}</p>}
                </div>
            </div>
        );
    }

}
export default MarkerCircle;
