import React from 'react';

import { View, Panel, Root, PanelHeader } from '@vkontakte/vkui';
import Navbar from './panels/Navbar';

import '@vkontakte/vkui/dist/vkui.css';
import './resources/styles/style.css';
import CreateNew from './pages/CreateNew';
import { getEpmtyModel } from './utils/Utils';



class Application extends React.Component {

    constructor(){
        super()
        this.state = {
            activeView : 'create',
            post : getEpmtyModel(),
            news : []

        }
        this.changePage = this.changePage.bind(this);
        this.setPost = this.setPost.bind(this);
        this.addNews = this.addNews.bind(this);

    }
    changePage(page){
        this.setState({activeView : page})
    }
    setPost(post){
        this.setState({post : post})
    }
    addNews(post){
        var news = this.state.news
        news.push(post)
        this.setState({news : news})
    }



    render() {
        return (
            <Root activeView={this.state.activeView}>
                <View id="create" activePanel="create_panel">
                    <Panel style={{backgroundColor : "white"}} id="create_panel">
                        <CreateNew addNews={this.addNews} setPost={this.setPost} post={this.state.post} setPodcast={this.setPodcast} changePage={this.changePage}/>
                    </Panel>
                </View>

            </Root>


        );
    }
}
export default Application;
